sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var obj = JSON.parse(inputs.json);
   var trfa = obj.Ci[0].attributes.create_or_pick;
  if(trfa == 2)
    {
      outputs.create_or_pick1 = true
    }
  else if(trfa == 1)
    {
    	 outputs.create_or_pick1 = false
    }

})(inputs, outputs);

