sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...


  var obj = JSON.parse(inputs.json);


  var gr = new GlideRecord(obj.Ci[0].class);
  var attr = obj.Ci[0].attributes
  gr.initialize();
  gr.name = attr["Auto calcualted name"];
  gr.owned_by = attr["Owned By"];
  gr.used_for = attr["Used for"];
  gr.busines_criticality = attr["Business Criticality"];
  gr.department = attr["Department"];
  gr.assignment_group =attr["Change Group"];
  gr.change_control = attr["Approval Group"];
  gr.support_group = attr["Support Group"];
  gr.short_description = attr["Description"];

  outputs.ci_id= gr.insert();





})(inputs, outputs);

