sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var requestBody;
var responseBody;
var status;
var sm;
var gr;
try{
       // Might throw exception if message doesn't exist or not visible due to scope.
	sm = new sn_ws.RESTMessageV2("Request Transaction Rest", "create_request");  

	gr = inputs.rec

  	var body ={'"request_payload":"'+gr.request_payload.toString()+'"'};
  	sm.setRequestBody(body);
	//sm.setXMLParameter("payload",gr.request_payload);
	outputs.logds = gr.request_payload.toString();

	response = sm.execute();	
    responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
	outputs.status = response.getStatusCode();
} catch(ex) {
	responseBody = ex.getMessage();
	outputs.status = '500';
} finally {
	requestBody = sm ? sm.getRequestBody():null;
}
})(inputs, outputs);

