sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  var obj = JSON.parse(inputs.json);

  if(obj.Ci[0].attributes.create_or_pick == 2)
  {

    var gr = new GlideRecord(obj.Ci[0].class);
    var attr = obj.Ci[0].attributes;
    gr.initialize();
    for (var key in attr){
      gr.setValue(key,attr[key]);

    }

    outputs.ci_id= gr.insert();


  }else if (obj.Ci[0].attributes.create_or_pick == 1){
    outputs.ci_id = obj.Ci[0].attributes.application_ser_ci;
  }

    var grCat = new GlideRecord(obj.Ci[1].class);
    var attrCat = obj.Ci[1].attributes;
    grCat.initialize();
    for(var keyCat in attrCat){
      grCat.setValue(keyCat,attrCat[keyCat]);
    }
    outputs.catitem = grCat.insert();

  var grRel = new GlideRecord("cmdb_rel_ci");
  grRel.initialize();
  grRel.parent = outputs.ci_id;
  grRel.type = obj.Relationship;
  grRel.child = outputs.catitem;
  outputs.rel = grRel.insert();

  outputs.ritmnumber = obj["RITM Number"];

})(inputs, outputs);

